arguments.argument:
{
    if (readyToShoot && !instance_exists(healTarget) && !ubering && ammoCount > 0) {
        ammoCount -= 1;
        playsound(x,y,MedichaingunSnd);
            var shot;
        shot = instance_create(x,y + yoffset + 1,Needle);
        shot.direction=owner.aimDirection;
        shot.speed=7+random(3);
        shot.owner=owner;
        shot.ownerPlayer=ownerPlayer;
        shot.team=owner.team;
        shot.hitDamage = 4;
        shot.weapon=WEAPON_NEEDLEGUN;
        justShot=true;
        with(shot)
            hspeed+=owner.hspeed;
        readyToShoot=false;
        alarm[0]=refireTime;
        alarm[5] = reloadBuffer + reloadTime;
    }
    else if (uberReady && (owner.keyState & $10)) {
        if(global.isHost) {
            sendEventUber(ownerPlayer);
            doEventUber(ownerPlayer);
        }
    }
}
